import React from 'react';
import styled from 'styled-components';

const Product = ({ id, title, img, price, rating }) => {
  return (
    <Products>
      <div>
        <p>{title}</p>
        <p className="produnct__pirce">
          <small>£</small>
          <strong>{price}</strong>
        </p>
        <div className="product__rating">
          {Array(rating)
            .fill()
            .map(() => (
              <p>⭐️</p>
            ))}
        </div>
        <img src={img} alt="" />
        <button>add to basket</button>
      </div>
    </Products>
  );
};

export default Product;

const Products = styled.div`
  display: flex;
  flex-direction: colmn;
  .product__rating {
    display: flex;
  }
  & > img {
    max-height: 300px;
    width: 100%;
    object-fit: contain;
    margin-bottom: 15px;
  }
`;
