import React from 'react';
import styled from 'styled-components';
import CurrencyFormat from 'react-currency-format';
import { useStateVaule } from './StateProvider';

const SubTotal = () => {
  const [{ basket }, dispatch] = useStateVaule();

  return (
    <SubTotals>
      <CurrencyFormat
        decimalScale={2}
        vaule={0}
        displayType={'text'}
        prefix={'$'}
        renderText={(value) => (
          <>
            <p>
              Sub Total ({basket.length} items) ; <strong>{`${value}`}</strong>
            </p>
            <small className="subtital__gift">
              <input type="checkbox" />
              This other contains a gift
            </small>
          </>
        )}
      />
      <button>Proceed to Checkout</button>
    </SubTotals>
  );
};

export default SubTotal;

const SubTotals = styled.div`
  display: flex;
  flex-direction: column;
  width: 300px;
  height: 100px;
  background-color: #f3f3f3;
  border: 1px solid #ddd;
  border-radius: 3px;
  .subtital__gift {
    display: flex;
    align-items: center;
  }
`;
