import React from 'react';
import { MdAdd } from 'react-icons/md';
import styled from 'styled-components';

const TodoInsert = () => {
  return (
    <TodoForm>
      <input placeholder="할일을 입력하세요" />
      <button type="submit">
        <MdAdd />
      </button>
    </TodoForm>
  );
};

export default TodoInsert;

const TodoForm = styled.form`
  display: flex;
  background-color: #ffffff;
  input {
    border: none;
    background: none;
    outline: none;
    padding: 0.5rem;
    font-size: 1.2rem;
    line-height: 2rem;
    color: #fff;
    &::placeholder {
      color: #aaa;
    }
    flex: 1;
  }
`;
