export const initialState = {
  basket: [
    {
      id: '757443',
      title:
        'Amazon Brand - Solimo Tall Kitchen Drawstring Trash Bags, 13 Gallon, 120 Count',
      price: 14.49,
      rating: 4.6,
      img:
        'https://images-na.ssl-images-amazon.com/images/I/41vaImNluxL._AC_US160_.jpg',
    },
  ],
  user: null,
};

function reducer(state, action) {
  switch (action.type) {
    case 'ADD_TO_BASKET':
      return { ...state, basket: [...state.basket, action.item] };

    case 'REMOVE_FROM_BASKET':
      let newBasket = [...state.basket];
      const index = state.basket.findIndex(
        (basketItem) => basketItem.id === action.id,
      );

      if ((index) => 0) {
        newBasket.splice(index, 1);
      } else {
        console.warn(`Cant remove product(${action.id}) as its not exist`);
      }
      return { ...state, basket: newBasket };

    default:
      return state;
  }
}

export default reducer;
